import React, { Component } from "react";
import PropTypes from "prop-types";
import styles from "./MessageBoard.scss";

class MessageBoard extends Component {
  state = {
    messages: [
      { author: "John Appleseed", team: "zombie", message: "hello world" },
      { author: "John Appleseed", team: "zombie", message: "hello world" },
      { author: "John Appleseed", team: "zombie", message: "hello world" }
    ]
  };
  componentDidMount() {
    // fetch messages and set state
  }

  render() {
    return (
      <div style={{ display: "flex", justifyContent: "center" }}>
        <div className="message-board" style={{ textAlign: "center" }}>
          <h2>Message Board</h2>
          <div className="messages-container">
            {this.state.messages.map(msg => (
              <div className="message">
                <div className="message__body">{msg.message}</div>
                <div className="message__caption">
                  <div className="message__author">{msg.author}</div>
                </div>
                {msg.team}
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default MessageBoard;
