        time: "2019-09-06 23:53:46"
      }
    ]
  };

  componentDidMount() {
    // fetch messages and set state
  }

  render() {
    const people = [
      "üë©",
      "üë©üèª",
      "üë©üèº",
      "üë©üèΩ",
      "üë©üèæ",
      "üë©üèø",
      "üë®",
      "üë®üèª",
      "üë®üèº",
      "üë®üèΩ",
      "üë®üèæ",
      "üë®üèø"
    ];
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          paddingTop: "64px"
        }}
      >
        <div className="message-board body" style={{ textAlign: "center" }}>
          <h2>Message Board</h2>
          <div className="messages-container">
            {this.state.messages.map(msg => (
              <div key={msg.id} className="message" style={{ display: "flex" }}>
                <div style={{ fontSize: "32px", paddingRight: "12px" }}>
                  {msg.team === "zombie"
                    ? "üßü"
                    : people[Math.floor(Math.random() * people.length)]}
                </div>
                <div>
                  <div className="message__body caption">{msg.message}</div>
                  <div className="message__caption">
                    <div className="message__author">
                      {msg.author} at {msg.time}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default MessageBoard;
