import React, { Component } from "react";
import PropTypes from "prop-types";
import styles from "./MessageBoard.css";

class MessageBoard extends Component {
  state = {
    messages: [
      { author: "John Appleseed", team: "zombie", message: "hello world" },
      { author: "John Appleseed", team: "zombie", message: "hello world" },
      { author: "John Appleseed", team: "zombie", message: "hello world" }
    ]
  };
  componentDidMount() {
    // fetch messages and set state
  }

  render() {
    return (
      <div>
        <h2>Message Board</h2>
        <div>
          {this.state.messages.map(msg => (<div>{msg.message}{msg.author}{msg.team}</div>))}
        </div>
      </div>
    )
  }
}

export default MessageBoard;
