import React, { Component } from "react";
import "./MessageBoard.scss";

class MessageBoard extends Component {
  state = {
    messages: [
      {
        id: 1,
        author: "John Appleseed",
        team: "zombie",
        message:
          "hello, i'm trying to infect more people. does anyone have any enemies I can send a token of infection to?",
        time: "2019-09-06 23:53:46"
      }
    ]
  };

  componentDidMount() {
    // fetch messages and set state
  }

  handleChange(e) {
    this.setState({ message: e.target.value });
  }

  handleSubmit(e) {
    alert(this.state.value);
  }

  render() {
    const people = [
      "üë©",
      "üë©üèª",
      "üë©üèº",
      "üë©üèΩ",
      "üë©üèæ",
      "üë©üèø",
      "üë®",
      "üë®üèª",
      "üë®üèº",
      "üë®üèΩ",
      "üë®üèæ",
      "üë®üèø"
    ];
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center"
        }}
      >
        <div className="message-board body">
          <div>
            <h3 style={{ marginBottom: "12px", marginLeft: "12px" }}>
              Message Board
            </h3>
          </div>
          <div className="messages-container">
            <div className="comment-box">
              <form type="POST" onSubmit={this.handleSubmit}>
                <textarea
                  onChange={this.handleChange}
                  placeholder="Add your message..."
                />
                <input type="submit" />
              </form>
            </div>
            {this.state.messages.map(msg => (
              <div key={msg.id} className="message" style={{ display: "flex" }}>
                <div style={{ fontSize: "32px", paddingRight: "12px" }}>
                  {msg.team === "zombie"
                    ? "üßü"
                    : people[Math.floor(Math.random() * people.length)]}
                </div>
                <div>
                  <div className="message__body caption">{msg.message}</div>
                  <div className="message__caption">
                    <div className="message__author caption disabled">
                      {msg.author} at {msg.time}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default MessageBoard;
